mapper.select:
select count(*) from tbl_tran t join tbl_user u on u.id = t.owner join tbl_customer cus on cus.id = t.customerId join tbl_contacts con on con.id = t.contactsId
and t.owner like '%' #{tran.owner} '%'
and t.name like '%' #{tran.name} '%'
and t.customerId like '%' #{tran.customerId} '%'
and t.stage = #{tran.stage}
and t.type = #{tran.type}
and t.source = #{tran.source}
and t.contactsId like '%' #{tran.contactsId} '%'

select t.id, u.name as owner, t.name, cus.name as customerId, t.stage, t.type, t.source, con.fullname as contactsId, t.createTime from tbl_tran t join tbl_user u on u.id = t.owner join tbl_customer cus on cus.id = t.customerId join tbl_contacts con on con.id = t.contactsId
and t.owner like '%' #{tran.owner} '%'
and t.name like '%' #{tran.name} '%'
and t.customerId like '%' #{tran.customerId} '%'
and t.stage = #{tran.stage}
and t.type = #{tran.type}
and t.source = #{tran.source}
and t.contactsId like '%' #{tran.contactsId} '%'
order by t.createTime desc limit #{skipCount},#{pageSize}
